#!/bin/bash
# Submodule Helper Scripts

# Function to update all submodules
update_all_submodules() {
  echo "Updating all submodules to latest..."
  git submodule update --remote
  git add .
  git commit -m "Update all submodules to latest versions"
}

# Function to check submodule status
check_submodule_status() {
  echo "=== Submodule Status ==="
  git submodule status

  echo "\n=== Submodule Summary ==="
  git submodule summary
}

# Function to execute command in all submodules
exec_in_submodules() {
  local command="$1"
  echo "Executing '$command' in all submodules..."
  git submodule foreach "$command"
}

# Function to pull latest in all submodules
pull_all_submodules() {
  echo "Pulling latest changes in all submodules..."
  git submodule foreach 'git pull origin main'
}

# Function to create feature branch in submodule
create_submodule_feature() {
  local submodule_path="$1"
  local branch_name="$2"

  if [ -z "$submodule_path" ] || [ -z "$branch_name" ]; then
    echo "Usage: create_submodule_feature <submodule_path> <branch_name>"
    return 1
  fi

  echo "Creating feature branch '$branch_name' in submodule '$submodule_path'..."
  cd "$submodule_path"
  git checkout -b "$branch_name"
  cd - > /dev/null
}

# Function to sync submodule with upstream
sync_submodule_upstream() {
  local submodule_path="$1"

  if [ -z "$submodule_path" ]; then
    echo "Usage: sync_submodule_upstream <submodule_path>"
    return 1
  fi

  echo "Syncing submodule '$submodule_path' with upstream..."
  cd "$submodule_path"
  git fetch upstream
  git checkout main
  git merge upstream/main
  git push origin main
  cd - > /dev/null
}

# Export functions
