const { passwordStrengthRegex, validateEnvironment } = require('../../src/backend/utils/validation');

describe('Password Validation', () => {
  test('should accept strong passwords', () => {
    const strongPasswords = [
      'SecurePass123!',
      'MyP@ssw0rd2024',
      'Tr0ub4dor&3'
    ];

    strongPasswords.forEach(password => {
      expect(passwordStrengthRegex.test(password)).toBe(true);
    });
  });

  test('should reject weak passwords', () => {
    const weakPasswords = [
      'password',        // no uppercase, numbers, special chars
      'PASSWORD',        // no lowercase, numbers, special chars
      '12345678',        // no letters, special chars
      'Passw0rd',        // no special chars
      'Pass123!',        // too short
      'passw@rd',        // no uppercase, numbers
    ];

    weakPasswords.forEach(password => {
      expect(passwordStrengthRegex.test(password)).toBe(false);
    });
  });
});

describe('Environment Validation', () => {
  const originalEnv = process.env;

  beforeEach(() => {
    jest.resetModules();
    process.env = { ...originalEnv };
  });

  afterAll(() => {
    process.env = originalEnv;
  });

  test('should pass with valid environment variables', () => {
    process.env.JWT_SECRET = 'this-is-a-very-secure-secret-key-with-32-chars';
    process.env.NODE_ENV = 'test';

    expect(() => validateEnvironment()).not.toThrow();
  });

  test('should fail with missing JWT_SECRET', () => {
    delete process.env.JWT_SECRET;
    process.env.NODE_ENV = 'test';

    expect(() => validateEnvironment()).toThrow('Missing required environment variables');
  });

  test('should fail with short JWT_SECRET', () => {
    process.env.JWT_SECRET = 'short';
    process.env.NODE_ENV = 'test';

    expect(() => validateEnvironment()).toThrow('JWT_SECRET must be at least 32 characters');
  });
});
