examples.code:
# main.tf
terraform {
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
}

provider "aws" {
  region = "us-east-1"
}

resource "aws_s3_bucket" "example" {
  bucket = "my-terraform-bucket-2024"

  tags = {
    Name        = "My bucket"
    Environment = "Dev"
  }
}

# Initialize and apply
# terraform init
# terraform plan
# terraform apply
